mapper.select:
select * 
from `order`
where orderID=#{orderID} and stationID=#{stationID}

select orderID,newspaperID,stationID,orderType,orderAddress,orderNum,orderDate
from `order`
where orderDate between #{startDate} and #{endDate} and customerID=#{customerID} and stationID=#{stationID}

select *
from `order`
where newspaperName like %#{newspaperName}% and stationID=#{stationID}

select *
from `order`
where customerName like %#{customerName}% and stationID=#{stationID}

select sum(orderNum)
from `order`
where orderDate = #{orderDate} and stationID=#{stationID}
